package javapractice;

public class Lec3forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//print value 0 to 9
		for (int i=0; i<=9; i++) {
			System.out.println(i);
		}
		//print value from 5 to 20
		for(int z=5; z<=20; z++) {
		System.out.println(z);
		}
			
		//print value 2 to 7
		for(int p= 2; p<=7; p++) {
			System.out.println(p);
			//print value  3 to 9
			for(int n=3; n<=9; n++) {
				System.out.println(n);
			}
				
				// find the value of even number from 0 to 21
			
			for(int c=2;c<21; c=c+2) {
				System.out.println(c);
			}
			// find the value of odd number from  2 to 11
			for(int d=3; d<11; d=d+3) {
				System.out.println(d);
			}
		
		}
	}
}
			
	

	

			
			
		
	
	


 